delete.if:
DELETE FROM questionnaire_student

    id in	/* 主键集合 */

        #{id, jdbcType=BIGINT}

mapper.select:
SELECT q.questionnaire_name as questionnaireName, q.questionnaire_code as questionnaireCode, q.term as term, ql.lesson_name as lessonName, tl.teacher_name as teacherName, q.questionnaire_end_time as questionnaireEndTime, qs.questionnaire_process_status_name as questionnaireProcessStatusName FROM questionnaire q LEFT JOIN questionnaire_lesson ql on q.questionnaire_code = ql.questionnaire_code and ql.is_delete =0 LEFT JOIN questionnaire_student qs on q.questionnaire_code = qs.questionnaire_code and qs.is_delete =0 LEFT JOIN teacher_lesson tl on tl.lesson_code = ql.lesson_code and tl.is_delete =0
q.is_delete = 0 AND qs.student_code = #{studentCode, jdbcType=VARCHAR}
AND q.questionnaire_status_code = #{questionnaireStatusCode, jdbcType=VARCHAR} /* 问卷状态编号（0:初始;1:已关联课程；2:已推送学生） */
AND qs.questionnaire_process_status_code = #{questionnaireProcessStatusCode, jdbcType=VARCHAR} /* 问卷进度 0：未答；1：进行中：2：答完 */
AND q.term = #{term, jdbcType=VARCHAR}

SELECT COUNT(1) FROM questionnaire q LEFT JOIN questionnaire_lesson ql on q.questionnaire_code = ql.questionnaire_code and ql.is_delete =0 LEFT JOIN questionnaire_student qs on q.questionnaire_code = qs.questionnaire_code and qs.is_delete =0 LEFT JOIN teacher_lesson tl on tl.lesson_code = ql.lesson_code and tl.is_delete =0
q.is_delete = 0 AND qs.student_code = #{studentCode, jdbcType=VARCHAR}
AND q.questionnaire_status_code = #{questionnaireStatusCode, jdbcType=VARCHAR} /* 问卷状态编号（0:初始;1:已关联课程；2:已推送学生） */
AND q.term = #{term, jdbcType=VARCHAR}
AND qs.questionnaire_process_status_code = #{questionnaireProcessStatusCode, jdbcType=VARCHAR} /* 问卷进度 0：未答；1：进行中：2：答完 */
